CREATE TABLE IF NOT EXISTS `diagnosis_treatment` (
  `id` int NOT NULL AUTO_INCREMENT COMMENT '诊疗项目 ID',
  `treatmentType` varchar(10) DEFAULT NULL COMMENT '诊疗项目类别',
  `treatmentNumber` varchar(25) DEFAULT NULL COMMENT '诊疗项目编码',
  `countryNumber` varchar(100) DEFAULT NULL COMMENT '诊疗项目国家编码',
  `treatmentName` varchar(255) DEFAULT NULL COMMENT '诊疗项目名称',
  `treatmentInfo` varchar(255) DEFAULT NULL COMMENT '诊疗项目说明',
  `treatmentExclude` varchar(255) DEFAULT NULL COMMENT '除外内容',
  `treatmentUnit` varchar(10) DEFAULT NULL COMMENT '计价单位',
  `treatmentPrice` decimal(10,2) DEFAULT NULL COMMENT '价格',
  `remark` varchar(255) DEFAULT NULL COMMENT '备注',
  PRIMARY KEY (`id`),
  KEY `idx_treatmentName` (`treatmentName`),
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='诊疗项目表';

	