-- 2.药品信息维护表
DROP TABLE IF EXISTS `drug_info`;

CREATE TABLE IF NOT EXISTS `drug_info`
(
    `id`               bigint NOT NULL AUTO_INCREMENT COMMENT 'ID',
    `insuranceType`    varchar(10)    DEFAULT NULL COMMENT '药品类型',
    `chinaName`        varchar(255)   DEFAULT NULL COMMENT '药品名称',
    `goodsName`        varchar(255)   DEFAULT NULL COMMENT '品名',
    `specifications`   varchar(255)   DEFAULT NULL COMMENT '规格',
    `drugUnit`         varchar(10)    DEFAULT NULL COMMENT '单位',
    `drugManufacturer` varchar(255)   DEFAULT NULL COMMENT '生产厂家',
    `drugPrice`        decimal(10, 2) DEFAULT NULL COMMENT '价格',
    `remarks`          varchar(1024)  DEFAULT NULL COMMENT '备注',
    PRIMARY KEY (`id`),
    KEY `idx_chinaName` (`chinaName`),
    KEY `idx_insuranceType` (`insuranceType`)
) ENGINE = InnoDB
  DEFAULT CHARSET = utf8mb4
    COMMENT = '药品信息表';

